def unique_digits(x: List[int]) -> List[int]:
    """Return a sorted list of all elements that has not any even digit.

    Input: A list of positive integers x.
        Output: Sorted list of such elements.

    For example:
        >>> unique_digits([15, 33, 1422, 1])
        [1, 15, 33]
    """
    @functools.lru_cache(maxsize=None)
    def helper(x: List[int], acc: List[int]):
        if len(x) == 0:
            return acc

        even = x[:len(x) // 2] + [x[-1]] + x[len(x) // 2:]
