def count_nums(arr):
    def sum_digits(n):
        if n < 0:
            return sum_digits(-n) % 9 + 1
        elif n == 0:
            return 0
        else:
            return (sum_digits(n // 10) + n % 10) % 9 + 1

    return sum(map(sum_digits, arr))

